    int ID =0;
     private void CountDep(){
        try {
            con = DriverManager.getConnection("jdbc:mysql://localhost:3306/hotel_manager","root","123");
            st = con.createStatement();
            rs = st.executeQuery("select Max(ID) from checkin");
            rs.next();
            ID = rs.getInt(1)+1;
            con.close();
        } catch (SQLException ex) {
            Logger.getLogger(CheckIn.class.getName()).log(Level.SEVERE, null, ex);
        }
    } 
     private void login(){
         try{
             con = DriverManager.getConnection("jdbc:mysql://localhost:3306/hotel_manager","root","123");
             String sql = "insert into login values(?,?)";
             pst =con.prepareStatement(sql);
             pst.setString(1, (String) number.getSelectedItem());
             pst.setInt(2, ranNum);
             int add = pst.executeUpdate();
         }catch(Exception e){
             e.printStackTrace();
         }
     }  private void status(){
        try {
            con = DriverManager.getConnection("jdbc:mysql://localhost:3306/hotel_manager","root","123");
            String sql = "Update room set Status = 'Booked' where RoomNumber = ?";
            pst = con.prepareStatement(sql);
            pst.setString(1,(String)number.getSelectedItem());
            int row = pst.executeUpdate();
        } catch (SQLException ex) {
            System.out.println(ex);
        }
     }
