    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        String mail  = email.getText();
        java.util.Date dateBefore = jDateChooser1.getDate();
        String date = myformat.format(dateBefore);
        java.util.Date dateAfter = jDateChooser2.getDate();
        String date2 = myformat.format(dateAfter);
        if(name.getText().equals("") && phone.getText().equals("") && email.getText().equals("") && card.getText().equals("") && card.getText().equals("")){
            JOptionPane.showConfirmDialog(null, "Please enter full");}
        while(true){
             Pattern p = Pattern.compile("^[a-zA-Z][a-zA-Z0-9]+@[a-zA-Z]+(\\.[a-zA-Z]+){1,3}$");
             if(p.matcher(mail).find() == false ){
               JOptionPane.showConfirmDialog(null, "Email is correct");
               break;
             }else {try{
            CountDep();
            con = DriverManager.getConnection("jdbc:mysql://localhost:3306/hotel_manager","root","123");
            String sql ="insert into `checkin` values(?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?)";
            pst = con.prepareStatement(sql);
            pst.setInt(1, ID);
            pst.setString(2, name.getText());
            pst.setString(3, (phone.getText()));
            pst.setString(4,(String)nat.getSelectedItem());
            pst.setString(5,(String)gender1.getSelectedItem());
            pst.setString(6,email.getText());
            pst.setInt(7, Integer.valueOf(card.getText()));
            pst.setString(8,add.getText());
            pst.setString(9, date);
            pst.setString(10,date2);
            pst.setString(11,(String)(children.getSelectedItem()));
            pst.setString(12,(String)(adult.getSelectedItem()));
            pst.setString(13,(String)(bed1.getSelectedItem()));
            pst.setString(14,(String)(type.getSelectedItem()));
            pst.setString(15,(String)(views.getSelectedItem()));
            pst.setString(16,(String)(number.getSelectedItem()));
            pst.setInt(17, Integer.valueOf(price1.getText()));
            int row = pst.executeUpdate();
            JOptionPane.showConfirmDialog(rootPane, "You have successfully registered the room");
            JOptionPane.showConfirmDialog(null, "Room:"+number.getSelectedItem()+" and Pass: "+ranNum, "Account", JOptionPane.DEFAULT_OPTION);
                  login();
                  status();
                  clear();
                  new Login().setVisible(true);
                  this.dispose();
            con.close();
        }catch(Exception ex){
            JOptionPane.showConfirmDialog(this, ex);}   
              break;}}
    }//GEN-LAST:event_jButton3ActionPerformed

